require('colors')
require('draftlog').into(console)
const figlet = require('figlet')

const warn = m => {
  console.log(`  \u26A0 ${m}`.yellow)
}
const info = m => {
  console.log(`  \u2714 ${m}`.green)
}
const error = m => {
  console.log(`  \u2716 ${m}`.red)
}

const watermark = () => console.log(`${figlet.textSync('ql', {
  font: 'Ghost',
  horizontalLayout: 'default',
  verticalLayout: 'default'
})}\n\n`)

module.exports = {
  warn,
  info,
  error,
  watermark,
}
