const Kind = require('../kind')
const SimpleSchema = require('simpl-schema').default

class Enum {
  constructor(config) {
    const ConfigShape = new SimpleSchema({
      name: String,
      values: {
        type: Array,
        optional: true,
        defaultValue: []
      },
      'values.$': String,
    })
    ConfigShape.validate(config)
    Object.assign(
      this,
      { kind: Kind.TYPE_ENUM },
      ConfigShape.clean(config)
    )
  }
}

module.exports = Enum
