const SimpleSchema = require('simpl-schema').default
const Kind = require('../kind')
const Path = require('./path')
const Field = require('./field')

class Input {
  constructor(config) {
    const ConfigShape = new SimpleSchema({
      name: String,
      fields: {
        type: Array,
        optional: true,
        defaultValue: [],
      },
      'fields.$': {
        type: Field,
        blackbox: true,
      }
    })
    ConfigShape.validate(config)
    Object.assign(
      this,
      { kind: Kind.TYPE_INPUT },
      ConfigShape.clean(config)
    )
  }
  addField(field) {
    if (!field instanceof Field)
      throw new Error('field must be an instance of Field')
    this.fields.push(field)
  }
  addPaths(paths) {
    paths.forEach(path => {
      if (!path instanceof Path)
        throw new Error('path must be an instance of Path')
    })
    this.paths = paths
  }
}

module.exports = Input

